% ResetBits (ver.1.1)%% Bits++ initialization function.%% - Make Bits++ receive T-Lock information% - Linearlize Bits++'s CLUT%% %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Usage:%     ResetBits;%% %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Created by Takehiro Nagai on 07/02/2007 (ver.1)% Modified by Takehiro Nagai on 07/05/2007 (ver.1.1)%   :change method of palette reset%function ResetBitsbitsconst;bitsplusplus(bitsInit, '');bitsplusplus(bitsSetVideoMode, bitsNOGAMMACORRECT);for i = 1 : 256    palette(i).a = i/256;    palette(i).b = i/256;    palette(i).c = i/256;        % don't work properly...    palette(i).a = (i-1)/256;    palette(i).b = (i-1)/256;    palette(i).c = (i-1)/256;endbitsplusplus(bitsPaletteWrite, palette, 0, 256);bitsplusplus(bitsSetVideoMode, bitsVIDEOENCODEDCOMMS);